number_dropadd.script:
				function onValueChanged()
					window.onHealthChanged();
				end
				function onInit()
					if super.super.onInit then
						super.super.onInit();
					end

					OptionsManager.registerCallback("WNDC", onValueChanged);
					onValueChanged();
				end
				function onClose()
					OptionsManager.unregisterCallback("WNDC", onValueChanged);
				end
				function onDrop(x, y, draginfo)
					if draginfo.getType() == "number" then
						local rActor = ActorManager.resolveActor(window.getDatabaseNode());
						local rRoll = {};
						rRoll.bSecret = false;
						rRoll.sType = "number";
						rRoll.sDesc = draginfo.getDescription();
						rRoll.nMod = draginfo.getNumberData();
						rRoll.aDice = {nil, {result = 0}};
						ActionDamage.onDamage(nil, rActor, rRoll);
					end

					return true;
				end

			function onValueChanged()
				window.onHealthChanged();
			end
			function onInit()
				if super.super.onInit then
					super.super.onInit();
				end

				OptionsManager.registerCallback("WNDC", onValueChanged);
				onValueChanged();
			end

			function onClose()
				OptionsManager.unregisterCallback("WNDC", onValueChanged);
			end

			function onDrop(x, y, draginfo)
				if draginfo.getType() == "number" then
					local rActor = ActorManager.resolveActor(window.getDatabaseNode());
					local rRoll = {};
					rRoll.bSecret = false;
					rRoll.sType = "number";
					rRoll.sDesc = draginfo.getDescription() .. " [TYPE: injury]";
					rRoll.nMod = draginfo.getNumberData();
					rRoll.aDice = {nil, {result = 0}};
					ActionDamage.onDamage(nil, rActor, rRoll);
				end

				return true;
			end
